mapper.select:
select
id,orderType,orderNo,hotelId,hotelName,orderStatus,`count`,checkInDate,payAmount,linkUserName,creationDate
from itrip_hotel_order


    and userId=#{userId}


    and orderType=#{orderType}


    and orderNo like CONCAT ('%',#{orderNo},'%')


    and orderStatus=#{orderStatus}


    and linkUserName like CONCAT ('%',#{linkUserName},'%')


    and creationDate >= #{startDate}


    and creationDate <= #{endDate}

  and creationDate between date_sub(now(),interval 1 year) and now()

order by creationDate desc

limit #{offset},#{pageSize}

select count(*) from itrip_hotel_order



    and userId=#{userId}


    and orderType=#{orderType}


    and orderNo like CONCAT ('%',#{orderNo},'%')


    and orderStatus=#{orderStatus}


    and linkUserName like CONCAT ('%',#{linkUserName},'%')


    and creationDate >= #{startDate}


    and creationDate <= #{endDate}

  and creationDate between date_sub(now(),interval 1 year) and now()

select o.id AS id, o.hotelId AS hotelId, o.hotelName AS hotelName, h.hotelLevel AS hotelLevel, h.address AS address, o.roomId AS roomId, r.roomTitle AS roomTitle, r.roomBedTypeId AS roomBedTypeId, o.count AS count, o.bookingDays AS bookingDays, o.checkInDate AS checkInDate, o.checkOutDate AS checkOutDate, o.specialRequirement AS specialRequirement, o.linkUserName AS linkUserName, o.payAmount AS payAmount, r.payType AS roomPayType, d.name as roomBedTypeName, r.isHavingBreakfast as isHavingBreakfast, dayofweek(o.checkInDate) as checkInWeek, dayofweek(o.checkOutDate) as checkOutWeek from itrip_hotel_order o,itrip_hotel h,itrip_hotel_room r,itrip_label_dic d
o.hotelId=h.id and o.roomId=r.id and d.id=r.roomBedTypeId
and o.id=#{id}
